tinymce.PluginManager.add("linebreak", function(a) {
	a.addCommand('mceLineBreak',function(){
        a.execCommand('mceInsertContent',true,"<br />\n")
	});
	a.addButton('linebreak',{
	    title:'Line break',
	    cmd:'mceLineBreak'
	}),
	a.addMenuItem("linebreak", {
		text : "Line break",
		icon : "linebreak",
		cmd : "mceLineBreak",
		context : "insert"
	})

});